import React, { useRef, useState, useEffect } from 'react';
import Chart from 'chart.js/auto';
import { reactLocalStorage } from 'reactjs-localstorage';
import axios from 'axios'; // Add Axios import
import * as d3 from 'd3';

const BACKEND_URL = "http://localhost:3000";


function Dashboard() {
  const [budgetData, setBudgetData] = useState([]);
  const [income, setIncome] = useState(0);
  const [savings, setSavings] = useState(0);
  const [title, setTitle] = useState('');
  const [budget, setBudget] = useState('');
  const [color, setColor] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  let lineChartInstance = null;
  const lineChartRef = useRef(null);

  useEffect(() => {
    const token = reactLocalStorage.get('jwt');
    if (token) {
      console.log(token);
      getBudget(token);
      
    } else {
      handleError('Token not available.');
    }
  }, []);
  useEffect(() => {
    createLineChart(savings, income, budgetData);
    renderPieChart(budgetData);
  }, [budgetData, income, savings]);

  const getBudget = async (token) => {
    try {
      const response = await fetch(`${BACKEND_URL}/get_budget`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ token }),
      });

      const data = await response.json();
      if (data && data.ok === 1) {
        setBudgetData(data.budgetData);
        setIncome(data.income);
        setSavings(data.savings);
      } else {
        handleError(data.message || "Error in data response.");
      }
    } catch (error) {
      handleError(`Network Error: ${error.message}`);
    }
  };

  const addToBudget = async () => {
    const token = reactLocalStorage.get('jwt');
    const requestBody = { title, budget, color };

    try {
      const response = await fetch(`${BACKEND_URL}/add_to_budget`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();
      if (data && data.ok === 1) {
        setBudgetData(data.budgetData);
      } else {
        handleError(data.message || "Error: Unknown error.");
      }
    } catch (error) {
      handleError(`Network Error: ${error.message}`);
    }
  };

  const deleteFromBudget = async (titleToDelete) => {
    const token = reactLocalStorage.get('jwt');

    try {
      const response = await fetch(`${BACKEND_URL}/delete_from_budget`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify({ title: titleToDelete }),
      });

      const data = await response.json();
      if (data && data.ok === 1) {
        setBudgetData(budgetData.filter(item => item.title !== titleToDelete));
      } else {
        handleError(data.message || "Error: Unknown error while deleting.");
      }
    } catch (error) {
      handleError(`Network Error: ${error.message}`);
    }
  };

  const updateIncome = async () => {
    const token = reactLocalStorage.get('jwt');
    const requestBody = { income };

    try {
      const response = await fetch(`${BACKEND_URL}/api/update_income`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();
      if (data && data.ok === 1) {
        setIncome(data.income);
      } else {
        handleError(data.message || "Error: Unknown error.");
      }
    } catch (error) {
      handleError(`Network Error: ${error.message}`);
    }
  };

  const updateSavings = async () => {
    const token = reactLocalStorage.get('jwt');
    const requestBody = { savings };

    try {
      const response = await fetch(`${BACKEND_URL}/api/update_savings`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`,
        },
        body: JSON.stringify(requestBody),
      });

      const data = await response.json();
      if (data && data.ok === 1) {
        setSavings(data.savings);
      } else {
        handleError(data.message || "Error: Unknown error.");
      }
    } catch (error) {
      handleError(`Network Error: ${error.message}`);
    }
  };

  const renderPieChart = (budgetData) => {
    const labels = budgetData.map((item) => item.Title);
    const data = budgetData.map((item) => item.Budget);

    const pieChartCanvas = document.getElementById('pieChartCanvas');

    if (pieChartCanvas) {
      const pieChartContext = pieChartCanvas.getContext('2d');

      if (pieChartContext.chart) {
        pieChartContext.chart.destroy();
      }

      if (budgetData.length === 0) {
        pieChartContext.clearRect(0, 0, pieChartCanvas.width, pieChartCanvas.height);
        return;
      }

      new Chart(pieChartContext, {
        type: 'pie',
        data: {
          labels: labels,
          datasets: [
            {
              data: data,
              backgroundColor: budgetData.map((item) => item.color),
            },
          ],
        },
      });
    }
  };



  const createLineChart = (savings, income, budgetData) => {
    if (!budgetData) {
      return;
    }

    const totalBudget = budgetData.reduce((acc, item) => acc + item.budget, 0);
    const profit = income - totalBudget;

    const data = {
      labels: ["Current", "Projected"],
      datasets: [{
        label: "Balance Projection",
        backgroundColor: profit > 0 ? "#ccffcc" : "#ffcccc",
        data: [savings, savings + (profit * 12)],
      }],
    };

    if (lineChartInstance) {
      lineChartInstance.destroy();
    }

    lineChartInstance = new Chart(lineChartRef.current, {
      type: 'line',
      data: data,
    });
  };

  const renderDonutChart = (budgetData) => {
    const width = 450,
      height = 450,
      margin = 40;

    const radius = Math.min(width, height) / 2 - margin;
    const svg = d3
      .select("#donutChart svg")
      .attr("width", width)
      .attr("height", height)
      .append("g")
      .attr("transform", `translate(${width / 2},${height / 2})`);

    const color = d3
      .scaleOrdinal()
      .domain(budgetData.map((item) => item.title))
      .range(budgetData.map((item) => item.color));

    const pie = d3
      .pie()
      .sort(null)
      .value((d) => d.budget);
    const data_ready = pie(budgetData);

    const arc = d3
      .arc()
      .innerRadius(radius * 0.5)
      .outerRadius(radius * 0.8);

    const outerArc = d3
      .arc()
      .innerRadius(radius * 0.9)
      .outerRadius(radius * 0.9);

    svg
      .selectAll("allSlices")
      .data(data_ready)
      .enter()
      .append("path")
      .attr("d", arc)
      .attr("fill", (d) => color(d.data.title))
      .attr("stroke", "white")
      .style("stroke-width", "2px")
      .style("opacity", 0.7);

    svg
      .selectAll("allPolylines")
      .data(data_ready)
      .enter()
      .append("polyline")
      .attr("stroke", "black")
      .style("fill", "none")
      .attr("stroke-width", 1)
      .attr("points", function (d) {
        const posA = arc.centroid(d);
        const posB = outerArc.centroid(d);
        const posC = outerArc.centroid(d);
        const midangle = d.startAngle + (d.endAngle - d.startAngle) / 2;
        posC[0] = radius * 0.90 * (midangle < Math.PI ? 1 : -1);
        return [posA, posB, posC];
      });

    svg
      .selectAll("allLabels")
      .data(data_ready)
      .enter()
      .append("text")
      .text((d) => d.data.title)
      .attr("transform", function (d) {
        const pos = outerArc.centroid(d);
        const midangle = d.startAngle + (d.endAngle - d.startAngle) / 2;
        pos[0] = radius * 0.90 * (midangle < Math.PI ? 1 : -1);
        return `translate(${pos})`;
      })
      .style("text-anchor", function (d) {
        const midangle = d.startAngle + 2 * (d.endAngle - d.startAngle) / 2;
        return midangle < Math.PI ? "start" : "end";
      });
  };

  const getBudgetForDonutChart = () => {
    const token = reactLocalStorage.get('jwt');
    axios.get(`${BACKEND_URL}/get_budget`, {
      headers: {
        'Authorization': `Bearer ${token}`,
      }
    })
    .then(function (res) {
      const budgetData = res.data.map((item) => ({
        title: item.title,
        budget: item.budget,
        color: item.color
      }));
      setBudgetData(budgetData);
    })
    .catch(function (error) {
      handleError(`Network Error: ${error.message}`);
    });
  };

  useEffect(() => {
    getBudgetForDonutChart();
  }, [budgetData]);

  const handleError = (message) => {
    setErrorMessage(message);
    setTimeout(() => {
      setErrorMessage('');
    }, 5000);
  };

  const handleInputChange = (event, setter) => {
    setter(event.target.value);
  };

  return (
    <main className="center" id="main">
      <div id="dashboard">
        <h1>Budget Dashboard</h1>

        <div id="budgetTableHolder">
          {budgetData.length === 0 ? (
            <p>You don't have any budget data yet! Get started by adding some budget data below.</p>
          ) : (
            <table id="budgetTable">
              <thead>
                <tr>
                  <th>Title</th>
                  <th>Budget</th>
                  <th>Color</th>
                  <th>Delete</th>
                </tr>
              </thead>
              <tbody>
                {budgetData.map((item, index) => (
                  <tr key={index}>
                    <td>{item.title}</td>
                    <td>${item.budget}</td>
                    <td>
                      <div style={{ width: `${item.budget}%`, backgroundColor: item.color }}></div>
                    </td>
                    <td style={{ color: item.color }}>{item.color}</td>
                    <td>
                      <button onClick={() => deleteFromBudget(item.title)}>Delete</button>
                    </td>
                  </tr>
                ))}
              </tbody>
            </table>
          )}
        </div>

        <div>
          <h2>Add to Budget</h2>
          <div id="addToBudget">
            <input
              type="text"
              placeholder="Title"
              value={title}
              onChange={(e) => handleInputChange(e, setTitle)}
            />
            <input
              type="number"
              placeholder="Budget"
              value={budget}
              onChange={(e) => handleInputChange(e, setBudget)}
            />
            <input
              type="text"
              placeholder="Color"
              value={color}
              onChange={(e) => handleInputChange(e, setColor)}
            />
            <button onClick={addToBudget}>Add</button>
          </div>
        </div>

        <div>
          <p id="errorMessage">{errorMessage}</p>
        </div>

        <div id="pieChartHolder">
          <canvas id="pieChartCanvas" width="400" height="400"></canvas>
          <h2>Pie Chart</h2>
          <canvas id="pieChart" width="400" height="400"></canvas>
        </div>

        <div id="lineChartHolder">
          <h2>Line Chart</h2>
          <canvas id="lineChart" width="400" height="400" ref={lineChartRef}></canvas>
        </div>

        <div id="donutChartHolder">
          <h2>Donut Chart</h2>
          <div id="donutChart">
            <svg width="450" height="450"></svg>
          </div>
        </div>

        <div>
          <h2>Budget Settings</h2>
          <div id="budgetSettings">
            <input
              type="number"
              placeholder="Income"
              value={income}
              onChange={(e) => handleInputChange(e, setIncome)}
            />
            <input
              type="number"
              placeholder="Savings"
              value={savings}
              onChange={(e) => handleInputChange(e, setSavings)}
            />
            <button onClick={updateIncome}>Update Income</button>
            <button onClick={updateSavings}>Update Savings</button>
          </div>
        </div>
      </div>
    </main>
  );
}

export default Dashboard;
